import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';

class ApiCall extends Component {
  constructor(props) {
    super(props);

    this.state = {
      temperature: null,
      time:null,
      zone:null,
    };
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.match.params.id !== this.props.match.params.id) {
      const id = nextProps.match.params.id
      this.loadConstruction( id );
    }
  }

  async componentDidMount() {
    const { match: { params } } = this.props;
    console.log(params.id)
    const ids = params.id;
    const results = (await axios.get('http://localhost:8081/'+ids)).data;
    this.setState({
        temperature : results.body.currently.temperature,
        time : results.body.currently.time,
        zone : results.body.timezone,
    });
    console.log(this.state.results);
  }

  render() {
    return (
      <div className="container">
        <div className="row">

        {this.state.results === null && <p>Please first select a country zone...</p>}
        <div className="card text-white bg-success mb-3">
          <div className="card-header">Temperatura y hora</div>
          <div className="card-body">
              <h4 className="card-title">{this.state.zone}</h4>
              <p className="card-text">{this.state.temperature} <br></br>
                                        {this.state.time}   </p>

            </div>

          </div>
        </div>
      </div>
    )
  }


}




export default ApiCall;
